Fault Triggering Test:
public void testBugReport() {
    CharSequence[] lookup = {"1 < 2", "1 &lt; 2"};
    LookupTranslator translator = new LookupTranslator(lookup);
    StringWriter writer = new StringWriter();
    CharSequence input = CharBuffer.wrap("1 < 2".toCharArray());
    try {
        translator.translate(input, 0, writer);
    } catch (IOException e) {
        e.printStackTrace();
    }
    String result = writer.toString();
    assertEquals("1 &lt; 2", result);
}
